class WorkerThread(QThread):
    def run(self):
        for x in range(48):
            notification.notify(title = "It's time for a small break!",
                                message = "Why don't you stretch a bit, move your body and rest your eyes :)",
                                app_name = "Prorest",
                                app_icon = "Meh.ico",
                                timeout = 60)
            time.sleep(1800)



